public ListNode addTwoNumbers(ListNode l1, ListNode l2) {ListNode resultHead = new ListNode(0);ListNode resultTail = resultHead;int carry = 0;while (l1 != null || l2 != null) {if (l1 != null) {carry += l1.val;l1 = l1.next;}if (l2 != null) {carry += l2.val;l2 = l2.next;}ListNode newNode = new ListNode(carry % 10);resultTail.next = newNode;resultTail = newNode;carry = carry / 10;}if (carry == 1) {resultTail.next = new ListNode(1);}return resultHead.next;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {Stack<Integer> s1 = new Stack<>();Stack<Integer> s2 = new Stack<>();while (l1 != null) {s1.push(l1.val);l1 = l1.next;}while (l2 != null) {s2.push(l2.val);l2 = l2.next;}int carryBit = 0;ListNode dummyHead = new ListNode(0);while (!s1.isEmpty() || !s2.isEmpty()) {if (!s1.isEmpty()) {carryBit += s1.pop();}if (!s2.isEmpty()) {carryBit += s2.pop();}ListNode newNode = new ListNode(carryBit % 10);ListNode dummyHeadNext = dummyHead.next;dummyHead.next = newNode;newNode.next = dummyHeadNext;carryBit = carryBit / 10;}if (carryBit == 1) {ListNode dummyHeadNext = dummyHead.next;ListNode newNode = new ListNode(1);newNode.next = dummyHeadNext;dummyHead.next = newNode;}return dummyHead.next;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {int sum = 0;ListNode result = new ListNode(0);ListNode cur = result;while (l1 != null || l2 != null) {if (l1 != null) {sum += l1.val;l1 = l1.next;}if (l2 != null) {sum += l2.val;l2 = l2.next;}cur.next = new ListNode(sum % 10);cur = cur.next;sum = sum / 10;}if (sum == 1) {cur.next = new ListNode(1);}return result.next;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {if (l1 == null)return l2;if (l2 == null)return l1;ListNode x = new ListNode(0);ListNode head = x;int carry = 0;while (l1 != null || l2 != null) {int val = 0;if (l1 == null && l2 != null)val = l2.val + carry;else if (l2 == null && l1 != null)val = l1.val + carry;elseval = l2.val + carry + l1.val;carry = val / 10;x.next = new ListNode(val % 10);if (l1 != null)l1 = l1.next;if (l2 != null)l2 = l2.next;x = x.next;}if (carry > 0) {x.next = new ListNode(carry);}return head.next;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {if (l1 == null)return l2;if (l2 == null)return l1;Stack<ListNode> s1 = new Stack<>();while (l1 != null) {s1.add(l1);l1 = l1.next;}Stack<ListNode> s2 = new Stack<>();while (l2 != null) {s2.add(l2);l2 = l2.next;}int carry = 0;ListNode x = null;while (!s1.isEmpty() || !s2.isEmpty()) {int val = 0;if (s1.isEmpty() && !s2.isEmpty()) {val = s2.pop().val + carry;} else if (s2.isEmpty() && !s1.isEmpty()) {val = s1.pop().val + carry;} else {val = s2.pop().val + carry + s1.pop().val;}carry = val / 10;if (x == null) {x = new ListNode(val % 10);} else {ListNode nn = new ListNode(val % 10);nn.next = x;x = nn;}}if (carry > 0) {ListNode nn = new ListNode(carry);nn.next = x;x = nn;}return x;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {if (l1 == null || l2 == null)return null;ListNode newNode = new ListNode(Integer.MIN_VALUE);ListNode dummy = newNode;int remainder = 0;while (l1 != null || l2 != null) {int sum = (l1 == null ? 0 : l1.val) + (l2 == null ? 0 : l2.val);newNode.next = new ListNode((sum + remainder) % 10);newNode = newNode.next;remainder = (sum + remainder) / 10;if (l1 != null)l1 = l1.next;if (l2 != null)l2 = l2.next;}if (remainder != 0)newNode.next = new ListNode(remainder);return dummy.next;}
public ListNode addTwoNumbers(ListNode l1, ListNode l2) {ListNode dummyHead = new ListNode(0);ListNode p = l1;ListNode q = l2;ListNode curr = dummyHead;int carry = 0;while (p != null || q != null) {int x = (p != null) ? p.val : 0;int y = (q != null) ? q.val : 0;int sum = carry + x + y;carry = sum / 10;curr.next = new ListNode(sum % 10);curr = curr.next;if (p != null)p = p.next;if (q != null)q = q.next;}if (carry > 0) {curr.next = new ListNode(carry);}return dummyHead.next;}

