public static ChronoSeries of(@NotNull InfluxDB influxDB, @NotNull String database, @NotNull String table, @NotNull String column) {ChronoSeries series = new ChronoSeries();series.chronoScale = new ChronoScale();series.influxDB = requireNonNull(influxDB);series.database = requireNonNull(database);series.table = requireNonNull(table);series.column = requireNonNull(column);influxDB.setDatabase(database);QueryResult queryResult = series.influxDB.query(new Query(String.format("SELECT COUNT(\"%s\") FROM \"%s\"", column, table), database));for (QueryResult.Result result : queryResult.getResults()) {series.size = ((Double) result.getSeries().get(0).getValues().get(0).get(1)).intValue();if (series.size < 2) {throw new IllegalStateException("ChronoSeries requires at least two elements to initiate");}}queryResult = series.influxDB.query(new Query(String.format("SELECT FIRST(\"%s\") FROM \"%s\"", column, table), database));for (QueryResult.Result result : queryResult.getResults()) {String timeString = (String) result.getSeries().get(0).getValues().get(0).get(0);series.beginTimestamp = Instant.parse(timeString);}queryResult = series.influxDB.query(new Query(String.format("SELECT LAST(\"%s\") FROM \"%s\"", column, table), database));for (QueryResult.Result result : queryResult.getResults()) {String timeString = (String) result.getSeries().get(0).getValues().get(0).get(0);series.endTimestamp = Instant.parse(timeString);}LocalDateTime startDate = series.beginTimestamp.atZone(ZoneOffset.UTC).toLocalDateTime();LocalDateTime endDate = series.endTimestamp.atZone(ZoneOffset.UTC).toLocalDateTime();disableUnnecessaryUnits(series, startDate, endDate);return series;}
