public Time(String formattedTime) throws IllegalArgumentException {final Pattern minorTimeString = Pattern.compile("^\\d+$");final Pattern timeString = Pattern.compile("^((\\d+)w)?((\\d+)d)?((\\d+)h)?((\\d+)m)?((\\d+)s)?$");if (minorTimeString.matcher(formattedTime).matches()) {this.time += Long.parseUnsignedLong(formattedTime);return;}Matcher m = timeString.matcher(formattedTime);if (m.matches()) {this.time = amount(m.group(2), secondsPerWeek);this.time += amount(m.group(4), secondsPerDay);this.time += amount(m.group(6), secondsPerHour);this.time += amount(m.group(8), secondsPerMinute);this.time += amount(m.group(10), 1);}}
