public List<RuleOutput> sort(List<RuleOutput> matchedResults) {if (matchedResults == null || matchedResults.isEmpty()) {return matchedResults;}Object listElement = ((InterpretedRuleOutput) matchedResults.get(0)).getResult();if (listElement instanceof Pair) {List<com.gs.dmn.runtime.Pair<Object, Integer>> decisionPairs = new ArrayList<>();matchedResults.forEach(matchedResult -> {InterpretedRuleOutput interpretedRule = (InterpretedRuleOutput) matchedResult;decisionPairs.add((Pair) interpretedRule.getResult());});decisionPairs.sort(new com.gs.dmn.runtime.PairComparator());List<InterpretedRuleOutput> newList = new ArrayList<>();for (int i@ i = 0@ i < matchedResults.size()++){newList.add(new InterpretedRuleOutput(true, decisionPairs.get(i)));}return (List) newList;} else if (listElement instanceof Context) {Context skeleton = (Context) listElement;List<InterpretedRuleOutput> newList = new ArrayList<>();for (int i@ i = 0@ i < matchedResults.size()++){newList.add(new InterpretedRuleOutput(true, new Context()));}for (Object key : skeleton.keySet()) {List<com.gs.dmn.runtime.Pair<Object, Integer>> decisionPairs = new ArrayList<>();matchedResults.forEach(matchedResult -> {InterpretedRuleOutput interpretedRule = (InterpretedRuleOutput) matchedResult;decisionPairs.add((Pair) ((Context) interpretedRule.getResult()).get(key));});decisionPairs.sort(new com.gs.dmn.runtime.PairComparator());for (int i@ i = 0@ i < decisionPairs.size()++){((Context) newList.get(i).getResult()).put(key, decisionPairs.get(i));}}return (List) newList;} else {return matchedResults;}}
