void capturePoint(Vector3D beamOrigin, Vector3D beamDir, double distance, double intensity, double echo_width, int returnNumber, int pulseReturnNumber, int fullwaveIndex, String hitObjectId, int classification) {if (distance < detector.cfg_device_rangeMin_m) {return;}double precision = this.detector.cfg_device_accuracy_m / 2;double error = -precision + Math.random() * (precision * 2);error += boxMullerRandom(0.0, this.detector.cfg_device_accuracy_m / 2);distance += error;Vector3D pointPos = beamOrigin.add(beamDir.scalarMultiply(distance));Measurement m = new Measurement();m.position = pointPos;m.distance = distance;m.intensity = intensity;m.echo_width = echo_width;m.returnNumber = returnNumber;m.pulseReturnNumber = pulseReturnNumber;m.fullwaveIndex = fullwaveIndex;m.beamOrigin = beamOrigin;m.beamDirection = beamDir;m.hitObjectId = hitObjectId;m.classification = classification;detector.writeMeasurement(m);detector.mBuffer.add(m);}
private static String formatNumber(Context context, String number, String activeMcc, int networkType) {if (number == null) {throw new IllegalArgumentException("number is null");}if (activeMcc == null || activeMcc.trim().length() == 0) {throw new IllegalArgumentException("activeMcc is null or empty!");}String networkPortionNumber = PhoneNumberUtils.extractNetworkPortion(number);if (networkPortionNumber == null || networkPortionNumber.length() == 0) {throw new IllegalArgumentException("Number is invalid!");}NumberEntry numberEntry = new NumberEntry(networkPortionNumber);ArrayList<String> allIDDs = getAllIDDs(context, activeMcc);int nanpState = checkNANP(numberEntry, allIDDs);if (DBG)Rlog.d(TAG, "NANP type: " + getNumberPlanType(nanpState));if ((nanpState == NP_NANP_LOCAL) || (nanpState == NP_NANP_AREA_LOCAL) || (nanpState == NP_NANP_NDD_AREA_LOCAL)) {return networkPortionNumber;} else if (nanpState == NP_NANP_NBPCD_CC_AREA_LOCAL) {if (networkType == CDMA_HOME_NETWORK || networkType == CDMA_ROAMING_NETWORK) {return networkPortionNumber.substring(1);} else {return networkPortionNumber;}} else if (nanpState == NP_NANP_LOCALIDD_CC_AREA_LOCAL) {if (networkType == CDMA_HOME_NETWORK) {return networkPortionNumber;} else if (networkType == GSM_UMTS_NETWORK) {int iddLength = numberEntry.IDD != null ? numberEntry.IDD.length() : 0;return PLUS_SIGN + networkPortionNumber.substring(iddLength);} else if (networkType == CDMA_ROAMING_NETWORK) {int iddLength = numberEntry.IDD != null ? numberEntry.IDD.length() : 0;return networkPortionNumber.substring(iddLength);}}int internationalState = checkInternationalNumberPlan(context, numberEntry, allIDDs, NANP_IDD);if (DBG)Rlog.d(TAG, "International type: " + getNumberPlanType(internationalState));String returnNumber = null;switch(internationalState) {case NP_NBPCD_HOMEIDD_CC_AREA_LOCAL:if (networkType == GSM_UMTS_NETWORK) {returnNumber = networkPortionNumber.substring(1);}break;case NP_NBPCD_CC_AREA_LOCAL:returnNumber = NANP_IDD + networkPortionNumber.substring(1);break;case NP_LOCALIDD_CC_AREA_LOCAL:if (networkType == GSM_UMTS_NETWORK || networkType == CDMA_ROAMING_NETWORK) {int iddLength = numberEntry.IDD != null ? numberEntry.IDD.length() : 0;returnNumber = NANP_IDD + networkPortionNumber.substring(iddLength);}break;case NP_CC_AREA_LOCAL:int countryCode = numberEntry.countryCode;if (!inExceptionListForNpCcAreaLocal(numberEntry) && networkPortionNumber.length() >= 11 && countryCode != NANP_CC) {returnNumber = NANP_IDD + networkPortionNumber;}break;case NP_HOMEIDD_CC_AREA_LOCAL:returnNumber = networkPortionNumber;break;default:if (networkPortionNumber.startsWith(PLUS_SIGN) && (networkType == CDMA_HOME_NETWORK || networkType == CDMA_ROAMING_NETWORK)) {if (networkPortionNumber.startsWith(PLUS_SIGN + NANP_IDD)) {returnNumber = networkPortionNumber.substring(1);} else {returnNumber = NANP_IDD + networkPortionNumber.substring(1);}}}if (returnNumber == null) {returnNumber = networkPortionNumber;}return returnNumber;}
private static String formatNumber(Context context, String number, String activeMcc, int networkType) {if (number == null) {throw new IllegalArgumentException("number is null");} else if (activeMcc == null || activeMcc.trim().length() == 0) {throw new IllegalArgumentException("activeMcc is null or empty!");} else {String networkPortionNumber = PhoneNumberUtils.extractNetworkPortion(number);if (networkPortionNumber == null || networkPortionNumber.length() == 0) {throw new IllegalArgumentException("Number is invalid!");}NumberEntry numberEntry = new NumberEntry(networkPortionNumber);ArrayList<String> allIDDs = getAllIDDs(context, activeMcc);int nanpState = checkNANP(numberEntry, allIDDs);if (DBG) {Rlog.d(TAG, "NANP type: " + getNumberPlanType(nanpState));}if (nanpState == NP_NANP_LOCAL || nanpState == NP_NANP_AREA_LOCAL || nanpState == NP_NANP_NDD_AREA_LOCAL) {return networkPortionNumber;}if (nanpState != NP_NANP_NBPCD_CC_AREA_LOCAL) {if (nanpState == NP_NANP_LOCALIDD_CC_AREA_LOCAL) {if (networkType == NP_NANP_LOCAL) {return networkPortionNumber;}if (networkType == 0) {return PLUS_SIGN + networkPortionNumber.substring(numberEntry.IDD != null ? numberEntry.IDD.length() : NP_NONE);} else if (networkType == NP_NANP_AREA_LOCAL) {return networkPortionNumber.substring(numberEntry.IDD != null ? numberEntry.IDD.length() : NP_NONE);}}int internationalState = checkInternationalNumberPlan(context, numberEntry, allIDDs, NANP_IDD);if (DBG) {Rlog.d(TAG, "International type: " + getNumberPlanType(internationalState));}String returnNumber = null;switch(internationalState) {case NP_NBPCD_HOMEIDD_CC_AREA_LOCAL:if (networkType == 0) {returnNumber = networkPortionNumber.substring(NP_NANP_LOCAL);break;}break;case NP_HOMEIDD_CC_AREA_LOCAL:returnNumber = networkPortionNumber;break;case NP_NBPCD_CC_AREA_LOCAL:returnNumber = NANP_IDD + networkPortionNumber.substring(NP_NANP_LOCAL);break;case NP_LOCALIDD_CC_AREA_LOCAL:if (networkType == 0 || networkType == NP_NANP_AREA_LOCAL) {returnNumber = NANP_IDD + networkPortionNumber.substring(numberEntry.IDD != null ? numberEntry.IDD.length() : NP_NONE);break;}case NP_CC_AREA_LOCAL:int countryCode = numberEntry.countryCode;if (!(inExceptionListForNpCcAreaLocal(numberEntry) || networkPortionNumber.length() < NANP_LONG_LENGTH || countryCode == NP_NANP_LOCAL)) {returnNumber = NANP_IDD + networkPortionNumber;break;}default:if (networkPortionNumber.startsWith(PLUS_SIGN) && (networkType == NP_NANP_LOCAL || networkType == NP_NANP_AREA_LOCAL)) {if (!networkPortionNumber.startsWith("+011")) {returnNumber = NANP_IDD + networkPortionNumber.substring(NP_NANP_LOCAL);break;}returnNumber = networkPortionNumber.substring(NP_NANP_LOCAL);break;}}if (returnNumber == null) {returnNumber = networkPortionNumber;}return returnNumber;} else if (networkType == NP_NANP_LOCAL || networkType == NP_NANP_AREA_LOCAL) {return networkPortionNumber.substring(NP_NANP_LOCAL);} else {return networkPortionNumber;}}}
