public static final List itemReturnQuery(String itemName, int categoryId, int selectedReceiverType, int receiverId, int returnedStatus, String transferNumber, String returnNumber, Date fromDate, Date toDate) throws Exception {Criteria c = getSession().createCriteria(ItemReturn.class);c.add(Restrictions.eq("dFlag", 1));c.createAlias("transfer", "nik");c.createAlias("transfer.item", "it");if (!StringUtils.isEmpty(transferNumber)) {c.add(Restrictions.eq("nik.transferPanaNumber", transferNumber));}if (!StringUtils.isEmpty(returnNumber)) {c.add(Restrictions.eq("returnNumber", returnNumber));}if (selectedReceiverType > 0) {c.add(Restrictions.eq("nik.transferType", selectedReceiverType));}if (returnedStatus >= 0) {c.add(Restrictions.eq("nik.status", returnedStatus));}if (receiverId > 0) {if (selectedReceiverType == Transfer.PERSONNAL) {c.createAlias("transfer.person", "per");c.add(Restrictions.eq("per.id", receiverId));} else if (selectedReceiverType == Transfer.OFFICIAL) {c.createAlias("transfer.branchOffice", "bro");c.add(Restrictions.eq("bro.id", receiverId));}}if (!StringUtils.isEmpty(itemName)) {c.add(Restrictions.ilike("it.name", "%" + itemName.toLowerCase() + "%", MatchMode.ANYWHERE));}c.createAlias("it.category", "cat");c.add(Restrictions.eq("cat.categoryType", Category.TYPE_RETURNABLE));if (categoryId > 0) {c.add(Restrictions.eq("cat.id", categoryId));}if (!DateTimeUtils.isEmpty(fromDate)) {c.add(Restrictions.ge("addedDate", fromDate));}if (!DateTimeUtils.isEmpty(toDate)) {c.add(Restrictions.le("addedDate", toDate));}return c.list();}
