public static boolean isValid(@NonNull View view, @IntRange(from = 0) long duration) {long curTime = System.currentTimeMillis();Object tag = view.getTag(TAG_KEY);if (!(tag instanceof Long)) {view.setTag(TAG_KEY, curTime);return true;}long preTime = (Long) tag;if (curTime - preTime <= duration)return false;view.setTag(TAG_KEY, curTime);return true;}
public static boolean setBrightness(@IntRange(from = 0, to = 255) final int brightness) {ContentResolver resolver = Utils.getApp().getContentResolver();boolean b = Settings.System.putInt(resolver, Settings.System.SCREEN_BRIGHTNESS, brightness);resolver.notifyChange(Settings.System.getUriFor("screen_brightness"), null);return b;}
public static int setRedComponent(@ColorInt int color, @IntRange(from = 0x0, to = 0xFF) int red) {return (color & 0xff00ffff) | (red << 16);}
public static int setRedComponent(@ColorInt int color, @FloatRange(from = 0, to = 1) float red) {return (color & 0xff00ffff) | ((int) (red * 255.0f + 0.5f) << 16);}
private static DisplayMetrics getMiuiTmpMetrics(Resources resources) {if (!isInitMiui) {DisplayMetrics ret = null;String simpleName = resources.getClass().getSimpleName();if ("MiuiResources".equals(simpleName) || "XResources".equals(simpleName)) {try {mTmpMetricsField = Resources.class.getDeclaredField("mTmpMetrics");mTmpMetricsField.setAccessible(true);ret = (DisplayMetrics) mTmpMetricsField.get(resources);} catch (Exception e) {Log.e("AdaptScreenUtils", "no field of mTmpMetrics in resources.");}}isInitMiui = true;return ret;}if (mTmpMetricsField == null)return null;try {return (DisplayMetrics) mTmpMetricsField.get(resources);} catch (Exception e) {return null;}}
