public static final String sha256Sign(Map<String, Object> paramMap, String key) {try {String payParam = getSignTemp(paramMap, key);Mac sha256_HMAC = Mac.getInstance("HmacSHA256");SecretKeySpec secret_key = new SecretKeySpec(key.getBytes("UTF-8"), "HmacSHA256");sha256_HMAC.init(secret_key);byte[] array = sha256_HMAC.doFinal(payParam.getBytes("UTF-8"));StringBuilder sb = new StringBuilder();for (byte item : array) {sb.append(Integer.toHexString((item & 0xFF) | 0x100).substring(1, 3));}String sign = sb.toString().toUpperCase();logger.info("HMAC-SHA256{}", sign);return sign;} catch (Exception e) {logger.error("HMAC-SHA256{}", e);return null;}}
public static String htmlEncode(final CharSequence input) {if (input == null || input.length() == 0)return "";StringBuilder sb = new StringBuilder();char c;for (int i@ i = 0, len = @ input.length()@ i < len++){c = input.charAt(i);switch(c) {case '<':sb.append("&lt");break;case '>':sb.append("&gt");break;case '&':sb.append("&amp");break;case '\'':(the apostrophe, U+0027) wasinstead of &aposto work as expected in HTML 4sb.append("&#39");break;case '"':sb.append("&quot");break;default:sb.append(c);}}return sb.toString();}
private List<ReconciliationEntityVo> parseSuccess(List<String> list, String billDate, RpAccountCheckBatch batch) {String title = ",ID,,,,,,,,,,,,,,,,";String totalTitle = ",,,,";Pattern titlePattern = Pattern.compile("(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?),(.*?)$");Pattern pattern = Pattern.compile("^`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?),`(.*?)$");Pattern totalTitlePattern = Pattern.compile("(.*?),(.*?),(.*?),(.*?),(.*?)$");Pattern totalPattern = Pattern.compile("^`(.*?),`(.*?),`(.*?),`(.*?),`(.*?)$");String titleRawData = list.remove(0);if (!titlePattern.matcher(titleRawData).find()) {batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", rawdata[" + titleRawData + "], [" + title + "]");return null;}String totalRawData = list.remove(list.size() - 1);String totalTitleRawData = list.remove(list.size() - 1);if (!totalTitlePattern.matcher(totalTitleRawData).find()) {batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", rawdata[" + totalTitleRawData + "], [" + totalTitle + "]");return new ArrayList<ReconciliationEntityVo>();}Matcher totalMatcher = totalPattern.matcher(totalRawData);if (totalMatcher.find()) {String totalCount = totalMatcher.group(1);String totalAmountStr = totalMatcher.group(2);String refundAmountStr = totalMatcher.group(3);String feeAmountStr = totalMatcher.group(5);try {batch.setBankTradeCount(Integer.parseInt(totalCount));batch.setBankTradeAmount(new BigDecimal(totalAmountStr));batch.setBankRefundAmount(new BigDecimal(refundAmountStr));batch.setBankFee(new BigDecimal(feeAmountStr));} catch (NumberFormatException e) {LOG.warn(", billDate[" + billDate + "], billType[SUCCESS], rawdata[" + totalRawData + "]", e);batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", rawdata[" + totalRawData + "]");batch.setBankTradeCount(null);batch.setBankTradeAmount(null);batch.setBankRefundAmount(null);batch.setBankFee(null);return null;}} else {batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", rawdata[" + totalRawData + "]");return new ArrayList<ReconciliationEntityVo>();}List<ReconciliationEntityVo> entityVoList = new ArrayList<ReconciliationEntityVo>();for (String rawData : list) {ReconciliationEntityVo entityVo = new ReconciliationEntityVo();entityVoList.add(entityVo);Matcher matcher = pattern.matcher(rawData);if (matcher.find()) {String tradeTimeStr = matcher.group(1);String bankTrxNo = matcher.group(6);String bankOrderNo = matcher.group(7);String bankTradeStatus = matcher.group(10);String orderAmount = matcher.group(13);String bankFee = matcher.group(17);try {SimpleDateFormat sdf = new SimpleDateFormat(DATE_FORMAT_STYLE);entityVo.setOrderTime(sdf.parse(tradeTimeStr));} catch (ParseException e) {LOG.warn(", billDate[" + billDate + "], billType[SUCCESS], tradeTime[" + tradeTimeStr + "], rawdata[" + rawData + "]", e);batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", tradeTime[" + tradeTimeStr + "], rawdata[" + rawData + "]");return null;}entityVo.setBankTrxNo(bankTrxNo);entityVo.setBankOrderNo(bankOrderNo);entityVo.setBankTradeStatus(bankTradeStatus);entityVo.setBankAmount(new BigDecimal(orderAmount));entityVo.setBankFee(new BigDecimal(bankFee));} else {batch.setStatus(BatchStatusEnum.FAIL.name());batch.setCheckFailMsg(", rawdata[" + rawData + "]");return null;}}return entityVoList;}
