private void add(Delete base, TransactionResult result) {if (result == null) {throw new InvalidUsageException("the record to be deleted must be existing " + "and read beforehand");}Put put = new Put(base.getPartitionKey(), getClusteringKey(base, result).orElse(null)).forNamespace(base.forNamespace().get()).forTable(base.forTable().get()).withConsistency(Consistency.LINEARIZABLE);List<Value> values = new ArrayList<>();values.add(Attribute.toIdValue(id));values.add(Attribute.toStateValue(TransactionState.DELETED));values.add(Attribute.toPreparedAtValue(current));values.addAll(createBeforeValues(base, result));int version = result.getVersion();values.add(Attribute.toVersionValue(version + 1));put.withCondition(new PutIf(new ConditionalExpression(VERSION, toVersionValue(version), Operator.EQ)));put.withValues(values);mutations.add(put);}

private static boolean zipFile(final File srcFile, String rootPath, final ZipOutputStream zos, final String comment) throws IOException {rootPath = rootPath + (isSpace(rootPath) ? "" : File.separator) + srcFile.getName();if (srcFile.isDirectory()) {File[] fileList = srcFile.listFiles();if (fileList == null || fileList.length <= 0) {ZipEntry entry = new ZipEntry(rootPath + '/');entry.setComment(comment);zos.putNextEntry(entry);zos.closeEntry();} else {for (File file : fileList) {if (!zipFile(file, rootPath, zos, comment))return false;}}} else {InputStream is = null;try {is = new BufferedInputStream(new FileInputStream(srcFile));ZipEntry entry = new ZipEntry(rootPath);entry.setComment(comment);zos.putNextEntry(entry);byte[] buffer = new byte[BUFFER_LEN];int len;while ((len = is.read(buffer, 0, BUFFER_LEN)) != -1) {zos.write(buffer, 0, len);}zos.closeEntry();} finally {if (is != null) {is.close();}}}return true;}
