public static boolean zipFiles(final Collection<File> srcFiles, final File zipFile, final String comment) throws IOException {if (srcFiles == null || zipFile == null)return false;ZipOutputStream zos = null;try {zos = new ZipOutputStream(new FileOutputStream(zipFile));for (File srcFile : srcFiles) {if (!zipFile(srcFile, "", zos, comment))return false;}return true;} finally {if (zos != null) {zos.finish();zos.close();}}}
