public static List<File> unzipFileByKeyword(final File zipFile, final File destDir, final String keyword) throws IOException {if (zipFile == null || destDir == null)return null;List<File> files = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();try {if (isSpace(keyword)) {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.err.println("entryName: " + entryName + " is dangerous!");continue;}if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}} else {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.out.println("entryName: " + entryName + " is dangerous!");continue;}if (entryName.contains(keyword)) {if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}}}} finally {zip.close();}return files;}

