public static List<String> getComments(final File zipFile) throws IOException {if (zipFile == null)return null;List<String> comments = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());comments.add(entry.getComment());}zip.close();return comments;}
public static List<String> getFilesPath(final File zipFile) throws IOException {if (zipFile == null)return null;List<String> paths = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();while (entries.hasMoreElements()) {String entryName = ((ZipEntry) entries.nextElement()).getName();if (entryName.contains("../")) {System.out.println("entryName: " + entryName + " is dangerous!");paths.add(entryName);} else {paths.add(entryName);}}zip.close();return paths;}
private static boolean unzipChildFile(final File destDir, final List<File> files, final ZipFile zip, final ZipEntry entry, final String name) throws IOException {File file = new File(destDir, name);files.add(file);if (entry.isDirectory()) {return createOrExistsDir(file);} else {if (!createOrExistsFile(file))return false;InputStream in = null;OutputStream out = null;try {in = new BufferedInputStream(zip.getInputStream(entry));out = new BufferedOutputStream(new FileOutputStream(file));byte[] buffer = new byte[BUFFER_LEN];int len;while ((len = in.read(buffer)) != -1) {out.write(buffer, 0, len);}} finally {if (in != null) {in.close();}if (out != null) {out.close();}}}return true;}
public static List<File> unzipFileByKeyword(final File zipFile, final File destDir, final String keyword) throws IOException {if (zipFile == null || destDir == null)return null;List<File> files = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();try {if (isSpace(keyword)) {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.err.println("entryName: " + entryName + " is dangerous!");continue;}if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}} else {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.out.println("entryName: " + entryName + " is dangerous!");continue;}if (entryName.contains(keyword)) {if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}}}} finally {zip.close();}return files;}
