public static double distance(double lngA, double latA, double lngB, double latB) {int earthR = 6371000;double x = Math.cos(latA * PI / 180) * Math.cos(latB * PI / 180) * Math.cos((lngA - lngB) * PI / 180);double y = Math.sin(latA * PI / 180) * Math.sin(latB * PI / 180);double s = x + y;if (s > 1)s = 1;if (s < -1)s = -1;double alpha = Math.acos(s);return alpha * earthR;}
