private static boolean isSpace(final String s) {if (s == null)return true;for (int i@ i = 0, len = s.length()@ i < len++){if (!Character.isWhitespace(s.charAt(i))) {return false;}}return true;}
private static boolean zipFile(final File srcFile, String rootPath, final ZipOutputStream zos, final String comment) throws IOException {rootPath = rootPath + (isSpace(rootPath) ? "" : File.separator) + srcFile.getName();if (srcFile.isDirectory()) {File[] fileList = srcFile.listFiles();if (fileList == null || fileList.length <= 0) {ZipEntry entry = new ZipEntry(rootPath + '/');entry.setComment(comment);zos.putNextEntry(entry);zos.closeEntry();} else {for (File file : fileList) {if (!zipFile(file, rootPath, zos, comment))return false;}}} else {InputStream is = null;try {is = new BufferedInputStream(new FileInputStream(srcFile));ZipEntry entry = new ZipEntry(rootPath);entry.setComment(comment);zos.putNextEntry(entry);byte[] buffer = new byte[BUFFER_LEN];int len;while ((len = is.read(buffer, 0, BUFFER_LEN)) != -1) {zos.write(buffer, 0, len);}zos.closeEntry();} finally {if (is != null) {is.close();}}}return true;}
public static boolean zipFile(final File srcFile, final File zipFile, final String comment) throws IOException {if (srcFile == null || zipFile == null)return false;ZipOutputStream zos = null;try {zos = new ZipOutputStream(new FileOutputStream(zipFile));return zipFile(srcFile, "", zos, comment);} finally {if (zos != null) {zos.close();}}}
public static boolean zipFiles(final Collection<String> srcFilePaths, final String zipFilePath, final String comment) throws IOException {if (srcFilePaths == null || zipFilePath == null)return false;ZipOutputStream zos = null;try {zos = new ZipOutputStream(new FileOutputStream(zipFilePath));for (String srcFile : srcFilePaths) {if (!zipFile(getFileByPath(srcFile), "", zos, comment))return false;}return true;} finally {if (zos != null) {zos.finish();zos.close();}}}
public static boolean zipFiles(final Collection<File> srcFiles, final File zipFile, final String comment) throws IOException {if (srcFiles == null || zipFile == null)return false;ZipOutputStream zos = null;try {zos = new ZipOutputStream(new FileOutputStream(zipFile));for (File srcFile : srcFiles) {if (!zipFile(srcFile, "", zos, comment))return false;}return true;} finally {if (zos != null) {zos.finish();zos.close();}}}
public static List<String> getComments(final File zipFile) throws IOException {if (zipFile == null)return null;List<String> comments = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());comments.add(entry.getComment());}zip.close();return comments;}
public static List<String> getFilesPath(final File zipFile) throws IOException {if (zipFile == null)return null;List<String> paths = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();while (entries.hasMoreElements()) {String entryName = ((ZipEntry) entries.nextElement()).getName();if (entryName.contains("../")) {System.out.println("entryName: " + entryName + " is dangerous!");paths.add(entryName);} else {paths.add(entryName);}}zip.close();return paths;}
private static boolean unzipChildFile(final File destDir, final List<File> files, final ZipFile zip, final ZipEntry entry, final String name) throws IOException {File file = new File(destDir, name);files.add(file);if (entry.isDirectory()) {return createOrExistsDir(file);} else {if (!createOrExistsFile(file))return false;InputStream in = null;OutputStream out = null;try {in = new BufferedInputStream(zip.getInputStream(entry));out = new BufferedOutputStream(new FileOutputStream(file));byte[] buffer = new byte[BUFFER_LEN];int len;while ((len = in.read(buffer)) != -1) {out.write(buffer, 0, len);}} finally {if (in != null) {in.close();}if (out != null) {out.close();}}}return true;}
public static List<File> unzipFileByKeyword(final File zipFile, final File destDir, final String keyword) throws IOException {if (zipFile == null || destDir == null)return null;List<File> files = new ArrayList<>();ZipFile zip = new ZipFile(zipFile);Enumeration<?> entries = zip.entries();try {if (isSpace(keyword)) {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.err.println("entryName: " + entryName + " is dangerous!");continue;}if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}} else {while (entries.hasMoreElements()) {ZipEntry entry = ((ZipEntry) entries.nextElement());String entryName = entry.getName();if (entryName.contains("../")) {System.out.println("entryName: " + entryName + " is dangerous!");continue;}if (entryName.contains(keyword)) {if (!unzipChildFile(destDir, files, zip, entry, entryName))return files;}}}} finally {zip.close();}return files;}
private static boolean createOrExistsFile(final File file) {if (file == null)return false;if (file.exists())return file.isFile();if (!createOrExistsDir(file.getParentFile()))return false;try {return file.createNewFile();} catch (IOException e) {e.printStackTrace();return false;}}

