public static String byte2FitMemorySize(final long byteSize) {if (byteSize < 0) {return "shouldn't be less than zero!";} else if (byteSize < MemoryConstants.KB) {return String.format("%.3fB", (double) byteSize);} else if (byteSize < MemoryConstants.MB) {return String.format("%.3fKB", (double) byteSize / MemoryConstants.KB);} else if (byteSize < MemoryConstants.GB) {return String.format("%.3fMB", (double) byteSize / MemoryConstants.MB);} else {return String.format("%.3fGB", (double) byteSize / MemoryConstants.GB);}}
private static String byte2FitMemorySize(final long byteNum) {if (byteNum < 0) {return "shouldn't be less than zero!";} else if (byteNum < 1024) {return String.format(Locale.getDefault(), "%.3fB", (double) byteNum);} else if (byteNum < 1048576) {return String.format(Locale.getDefault(), "%.3fKB", (double) byteNum / 1024);} else if (byteNum < 1073741824) {return String.format(Locale.getDefault(), "%.3fMB", (double) byteNum / 1048576);} else {return String.format(Locale.getDefault(), "%.3fGB", (double) byteNum / 1073741824);}}
private static String getForegroundProcessName() {ActivityManager am = (ActivityManager) Utils.getApp().getSystemService(Context.ACTIVITY_SERVICE);List<ActivityManager.RunningAppProcessInfo> pInfo = am.getRunningAppProcesses();if (pInfo != null && pInfo.size() > 0) {for (ActivityManager.RunningAppProcessInfo aInfo : pInfo) {if (aInfo.importance == ActivityManager.RunningAppProcessInfo.IMPORTANCE_FOREGROUND) {return aInfo.processName;}}}if (android.os.Build.VERSION.SDK_INT > android.os.Build.VERSION_CODES.LOLLIPOP) {PackageManager pm = Utils.getApp().getPackageManager();Intent intent = new Intent(Settings.ACTION_USAGE_ACCESS_SETTINGS);List<ResolveInfo> list = pm.queryIntentActivities(intent, PackageManager.MATCH_DEFAULT_ONLY);Log.i("ProcessUtils", list.toString());if (list.size() <= 0) {Log.i("ProcessUtils", "getForegroundProcessName: noun of access to usage information.");return "";}try {ApplicationInfo info = pm.getApplicationInfo(Utils.getApp().getPackageName(), 0);AppOpsManager aom = (AppOpsManager) Utils.getApp().getSystemService(Context.APP_OPS_SERVICE);if (aom.checkOpNoThrow(AppOpsManager.OPSTR_GET_USAGE_STATS, info.uid, info.packageName) != AppOpsManager.MODE_ALLOWED) {intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK);Utils.getApp().startActivity(intent);}if (aom.checkOpNoThrow(AppOpsManager.OPSTR_GET_USAGE_STATS, info.uid, info.packageName) != AppOpsManager.MODE_ALLOWED) {Log.i("ProcessUtils", "getForegroundProcessName: refuse to device usage stats.");return "";}UsageStatsManager usageStatsManager = (UsageStatsManager) Utils.getApp().getSystemService(Context.USAGE_STATS_SERVICE);List<UsageStats> usageStatsList = null;if (usageStatsManager != null) {long endTime = System.currentTimeMillis();long beginTime = endTime - 86400000 * 7;usageStatsList = usageStatsManager.queryUsageStats(UsageStatsManager.INTERVAL_BEST, beginTime, endTime);}if (usageStatsList == null || usageStatsList.isEmpty())return null;UsageStats recentStats = null;for (UsageStats usageStats : usageStatsList) {if (recentStats == null || usageStats.getLastTimeUsed() > recentStats.getLastTimeUsed()) {recentStats = usageStats;}}return recentStats == null ? null : recentStats.getPackageName();} catch (PackageManager.NameNotFoundException e) {e.printStackTrace();}}return "";}
